import React,{useState,useEffect,useContext} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import {useParams,useNavigate} from 'react-router-dom';
import {StudentContext} from '../App'



function EditStudents(props) {



  let context = useContext(StudentContext);

  let params = useParams();
  let navigate = useNavigate();
  let [name,setName] = useState("");
  let [email,setEmail] = useState("");
  let [mobile,setMobile] = useState("");
  let [cls,setCls] = useState(""); 

  useEffect(()=>{
    if(params.id<props.data.students.length)
    {
        getData();
    }
    else
    {
        alert("Selected Students is Not available") 
        navigate("/all-students")
    }
  },[])

  let getData = ()=>{
    setName(context.students[params.id].name)
    setEmail(context.students[params.id].email)
    setMobile(context.students[params.id].mobile)
    setCls(context.students[params.id].class)
}

let handleSubmit = ()=>{
    let newData = {name,email,mobile,"class":cls};
    let newArray = [...context.students];
    newArray.splice(params.id,1,newData)
    context.setStudents(newArray)

    navigate("/all-students")

}

  
  return(
        <Form>
            <Form.Group className="mb-3">
                    <Form.Label>Name</Form.Label> 
                    <Form.Control type="text" placeholder="Name" onChange={(e)=>setName(e.target.value)}/>
                </Form.Group>

            <Form.Group className="mb-3">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" onChange={(e)=>setEmail(e.target.value)}/>
                <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3">
                <Form.Label>Mobile</Form.Label>
                <Form.Control type="text" placeholder="Mobile" onChange={(e)=>setMobile(e.target.value)}/>
            </Form.Group>
            <Form.Group className="mb-3">
                <Form.Label>class</Form.Label>
                <Form.Control type="text" placeholder="Class" onChange={(e)=>setCls(e.target.value)}/>
            </Form.Group>
            <Button variant="primary" onClick={handleSubmit}>
                Update
            </Button>
        </Form>
    )
}

export default EditStudents;
